package hard;

public class ShortestPalindrome {

	public String shortestPalindrome(String s) {
		if (s == null || s.length() == 0) {
			return "";
		}
		String[][] r = new String[s.length()][s.length() + 1];

		for (int i = 0; i < s.length(); i++) {
			for (int j = 0; j <= s.length(); j++) {
				r[i][j] = "";
			}
		}
		for (int j = 2; j <= s.length(); j++) {
			for (int i = 0; i <= s.length() - j; i++) {
				if (r[i + 1][j - 2].equals("") && s.charAt(i) == s.charAt(i + j - 1)) {
					r[i][j] = "";
				} else if (r[i][j - 1].equals("")) {
					r[i][j] = String.valueOf(s.charAt(i + j - 1));
				} else {
					r[i][j] = String.valueOf(s.charAt(i + j - 1)) + r[i][j - 1];
				}
			}
		}
		return r[0][s.length()];
	}

	public static void main(String[] arg) {
		System.out.println(new ShortestPalindrome().shortestPalindrome(""));
	}
}
