class BinarySearchTree<X> {
	private Node<Integer> root;
	private int toleranceValue = 0;
	private int indexToleranceValue = 0;
	private boolean hasSatisifiedValue = false;

	BinarySearchTree(Integer rootElem, int k, int t) {
		this.root = new Node<Integer>(rootElem, null, null);
		this.toleranceValue = k;
		this.indexToleranceValue = t;
	}

	public void setRoot(Node<Integer> root2) {
		this.root = root2;
		
	}

	public Node<Integer> getRoot() {
		return this.root;
	}

	public boolean hasSatisifiedValue() {
		return this.hasSatisifiedValue;
	}

	public Node<Integer> insert(Node<Integer> n, int index, int[] a) {
		if (n == null) {
			n = new Node<Integer>(new Integer(index), null, null);
			return n;
		}
		int valueDiff = a[index] - a[(int) n.value];
		int indexDiff = index - (int) n.value;
		if (Math.abs(valueDiff) <= toleranceValue
				&& Math.abs(indexDiff) <= indexToleranceValue) {
			//n.left = insert(n.left, index, a);
			hasSatisifiedValue = true;
			return n;
		} else {
			n.right = insert(n.right, index, a);
		}
		return n;
	}

	public boolean isAnyLeftChildPresent(Node<Integer> node) {
		if (node == null) {
			return false;
		}
		if (node.left != null) {
			return true;
		} else {
			return isAnyLeftChildPresent(node.right);
		}
	}
}

class Node<X> {
	X value;
	Node<X> left;
	Node<X> right;

	Node(X x, Node<X> left, Node<X> right) {
		this.value = x;
		this.left = left;
		this.right = right;
	}

}

